var Person = require('./Person')
var bits = require('physics/bits');
var defaults = require('lodash.defaults');

var defaultActorParams = {
	name: 'enemy',
	shape: 'circle',
	radius: 25,
	mass: 1,
	friction: 0,
	x: 300,
	y: 200,
	categoryBits: bits.enemy,
	maskBits: bits.environment | bits.hero | bits.enemy,
	fixedRotation: true,
	linearDamping: 10,
	interval: 2000,
	color: 0xbb2222
};

function Enemy(params) {
	params = params || {};
	defaults(params, defaultActorParams);
	Person.call(this, params);
}

Enemy.prototype = Object.create(Person.prototype);


var material;
Enemy.prototype.getMaterial = function(color) {
	if(!material) {
		material = new THREE.MeshBasicMaterial({
			color: color
		})
	}
	return material;
}


module.exports = Enemy;