var initd = false;
var physics = require('physics');

var Hero = require('./Hero');
var Enemy = require('./Enemy');

var world = require('world');
var view = require('view');



function init() {
	if(initd) return;
	initd = true;

	var scene = world.scene;
	
	var hero = new Hero();
	exports.hero = hero;
	physics.setAvatar(hero);

	for(var i = 20; i > 0; i--) {
		new Enemy();
	}

	delete exports.init;
}

var exports = {
	init: init
};

module.exports = exports;