var initd = false;

var touch = require('input/touch');
var view = require('view');

// var draw = require('./draw');
var target = require('./target');
// var walk = require('./walk');

function registerGesture(gesture) {
	touch.registerHandler(gesture);
	view.registerCameraOffsetCallback(gesture.updateCameraOffset);
	return gesture;
}

function init() {
	if(initd) return;
	initd = true;

	exports.target = registerGesture(target);

	exports.onEnterFrame = onEnterFrame;

	delete exports.init;
}

function onEnterFrame() {
	touch.onEnterFrame();
}

var exports = {
	init: init
};

module.exports = exports;