var lerp = require('lerp');

function getRatioBetween(value, start, end, mutation) {
	var range = end - start;
	if(mutation !== undefined){
		var base = (value - start) / range;
		return lerp(
			base, 
			(start + range * Math.random()) / range, 
			Math.random() * mutation
		);
	} else {
		return (value - start) / range;
	}
}

module.exports = getRatioBetween;