var three = require('three');
var TestPlane = require('./TestPlane');

var poolees = [];
   
function init(num, asset){ 
      for (var p = 0; p <= num; p++)
      {   
          poolees.push(new TestPlane());
       };

       exports.acquire = acquire;
       exports.release = release;
       exports.poolees = poolees;
       delete exports.init;
}

function acquire()  {
   console.log('acquired');
  
   for (var p =0; p < poolees.length; p++) {
      if (!poolees[p].active) {
         poolees[p].active = true;
         return poolees[p];
      };
   };

} 

function release(poolee) {
  console.log("released");
  poolee.active = false;  
}
   
function createTestPlanes(num) {
   var t = [];
   for (var i=0; i <= num; i++) {
      var testPlane = new TestPlane();
      t.push(testPlane);
   }
   return t;
}


exports = {
    init: init
};

module.exports = exports;


