var assetsPath = 'assets/images/';
var three = require('three');

var texturesData = [
	'icon_boy.png',
	'icon_cat.png',
	'icon_dog.png',
	'icon_girl.png',
	'icon_wife.png'
];

var textures = {};

texturesData.forEach(function(fileName) {
	var texture = three.ImageUtils.loadTexture(assetsPath + fileName);
	textures[fileName] = texture;
});



function getSprite(fileName) {
	var geometry = new three.PlaneGeometry(1, 1);
	var texture = textures[fileName];
	var material = new three.MeshBasicMaterial({
		map: texture
	});
	var mesh = new three.Mesh(geometry, material);
	mesh.scale.set(texture.image.width, texture.image.height, 1);
	return mesh;
}

module.exports = {
	getSprite:getSprite

};
