module.exports = {
	forEach: function(arrayishObject, operation) {
		Array.prototype.forEach.call(arrayishObject, operation);
	},
	filter: function(arrayishObject, operation) {
		return Array.prototype.filter.call(arrayishObject, operation);
	},
	map: function(arrayishObject, operation) {
		return Array.prototype.map.call(arrayishObject, operation);
	}
}

