var resize = require('input-resize');
var three = require('three');
var screenfull = require('screenfull');
var EventUtil = require('browser-event-adder');
var world = require('world');
THREE = three;
var Checkerboard = require('threejs-texture-checkerboard');

function noop() {};
var initd = false;

var renderer;
var camera;
var cameraX = 0;
var cameraY = 0;
var width = 100;
var height = 100;


var testPlane;
var preRender = noop;

function onResize(w, h) {
	renderer.setSize(w, h);
	exports.width = width = w;
	exports.height = height = h;
	updateCamera();
}

function rafTick() {
	window.requestAnimationFrame(rafTick);
	preRender();
	renderer.render(world.scene, camera);

}

function setPreRender(callback) {
	preRender = callback;
}

function setCameraCenter(x, y, size) {
	cameraX = x;
	cameraY = y;
	cameraSize = size;
	updateCamera();
}

function init() {
	if(initd) return;
	initd = true;
	var canvas = require('canvas');

	renderer = new three.WebGLRenderer({
		canvas: canvas.canvas
	});

	var pixelRatio = window.devicePixelRatio;
	// var pixelRatio = 0.5;
	renderer.setPixelRatio(pixelRatio);

	exports.pixelRatio = pixelRatio;

	camera = new three.OrthographicCamera(0, 100, 100, 0, -100, 100);
	world.scene.add(camera);

	testPlane = new three.Mesh(
		new three.PlaneGeometry(10000, 10000, 1, 1),
		new three.MeshBasicMaterial({
			map: new Checkerboard(undefined, undefined, 100, 100),
		})
	);
	testPlane.position.set(5000, 5000, 0);
	world.scene.add(testPlane);

	resize.onResize.add(onResize);
	resize.bump(onResize);

	renderer.render(world.scene, camera);

	window.requestAnimationFrame(rafTick);

	EventUtil.addEvent(canvas.canvas, 'touchend', requestFullscreen);

	delete exports.init;
	exports.setPreRender = setPreRender;
	exports.setCameraCenter = setCameraCenter;
	exports.requestFullscreen = requestFullscreen;
}

function updateCamera() {
	camera.left = width * -0.5 + cameraX;
	camera.right = width * 0.5 + cameraX;
	camera.top = height * 0.5 + cameraY;
	camera.bottom = height * -0.5 + cameraY;
	camera.updateProjectionMatrix();
}

function requestFullscreen() {
	if (screenfull.enabled) {
		screenfull.request();
	}
}

var exports = {
	init: init
};

module.exports = exports;